package week1.day1;


public class Factorial {
	
	public static void main(String[] args) {
		int i,fact=1;
		
		// TODO Auto-generated method stub
		int num=5;
		for ( i= 1; i <= num; i++) {
			fact=fact*i;
			
		}
		System.out.println("factorial of given number is:"+fact);
		}
		

	}


